import  React, { Component } from  'react';
import  ContactManager  from  './ContactManager';

const  contactManager  =  new  ContactManager();

class  ContactList  extends  Component {

    constructor(props) {
        super(props);
        this.state  = {
            contacts: [],
            nextPageURL:  '',
            query: '',
            search: []
        };
        this.nextPage  =  this.nextPage.bind(this);
        this.handleDelete  =  this.handleDelete.bind(this);
        this.handleInputChange = this.handleInputChange.bind(this);
    }

    componentDidMount() {
        var  self  =  this;
        contactManager.getContacts().then(function (result) {
            self.setState({ contacts:  result.data, nextPageURL:  result.nextlink})
        });
    }
    handleInputChange = () => {
        this.setState({
            query: this.search.value
        })

        let result = this.filterArray(this.state.query);
        result.map( (item) => {
            if (item !== undefined) {

                // console.log(item)
                this.setState(
                    {
                        search: item
                    }
                )
            }
        }
         )

    }
    handleDelete(e,pk){
        var  self  =  this;
        contactManager.deleteContact({pk :  pk}).then(()=>{
            var  newArr  =  self.state.contacts.filter(function(obj) {
                return  obj.pk  !==  pk;
            });

            self.setState({contacts:  newArr})
        });
    }

    nextPage(){
        var  self  =  this;
        console.log(this.state.nextPageURL);        
        contactManager.getContactsByURL(this.state.nextPageURL).then((result) => {
            self.setState({ contacts:  result.data, nextPageURL:  result.nextlink})
        });
    }
    filterArray = () => {
            let result = this.state.contacts.map( (item) => {
                // if (item.first_name === this.state.query ){
                if (item.first_name.includes(this.state.query) || item.last_name.includes(this.state.query)){    
                return item;
                }
            }

            )
            return result
    }

    render() {
        console.log(typeof(this.state.search))
        return (
            <div  className="contacts--list">

                <div className="searchForm">
                <form>
                    <input type="text" id="filter" placeholder="Search for..." ref={input => this.search = input} onChange={this.handleInputChange}/>
                </form>

                </div>
                <table  className="table">
                <thead  key="thead">
                <tr >

                    <th>#</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Phone</th>
                    <th>Email</th>
                    <th>Description</th>
                    <th>Organization</th>
                    <th>Title</th>
                    <th>Created</th>


                </tr>
                </thead>
                <tbody>
                {

                this.state.search.map( (contact,i)  =>
                    <tr  key={i}>
                    <td>{contact.id}  </td>
                    <td>{contact.first_name}</td>
                    <td>{contact.last_name}</td>
                    <td>{contact.phone_number}</td>
                    <td>{contact.email}</td>
                    <td>{contact.description}</td>
                    <td>{contact.organization}</td>
                    <td>{contact.title}</td>
                    <td>{contact.createdAt}</td>



                    <td>
                    <button  className="btn btn-danger" onClick={(e)=>  this.handleDelete(e,contact.pk) }> Delete</button>
                    <a  href={"/contacts/" + contact.id}> Update</a>
                    </td>
                </tr>)
                }

                </tbody>
                </table>
                <button  className="btn btn-primary"  onClick=  {  this.nextPage  }>Next</button>

            </div>
            );
      }
    }
    export  default  ContactList;
